--База данных «Books». Вложенные запросы.
--GROUP BY & HAVING

--1. Отобразить все издательства, которые выпустили новинки после 2000 года.
select Izd, min([date]) as [Date]
from [dbo].[books]
where year([Date]) > '2000'
group by Izd 

--2. Отобразить книги, у которых количество страниц больше, чем среднее арифметическое число страниц всех книг.
select *
from [dbo].[books]
where Pages > (select AVG(pages) from [dbo].[books])
order by Pages

--3. Отобразить тематики и сумму страниц по тематикам, учитывая при этом только книги с ценой >50.
select Themes, sum(Pages) as pages
from [dbo].[books]
where Price > 50
group by Themes

--4. Отобразить все темы, у которых не указана категория.
select Themes
from [dbo].[books]
where Category is null
group by Themes

--5. Отобразить самую дорогую книгу издательства BHV (2 способа).
select TOP 1 [Name]
from [dbo].[books]
where Izd like 'BHV%'
order by Price desc

select [Name]
from [dbo].[books]
where Izd like 'BHV%' and Price = (
	select Max(Price) 
	from [dbo].[books] 
	where Izd like 'BHV%'
)

--6. Отобразить издательство, у которого наибольшее количество страниц (2 способа).
select top 1 Izd, sum(Pages) as Pages
from [dbo].[books]
group by Izd
order by Pages desc

select Izd, sum(Pages) as Pages
from [dbo].[books]
group by Izd
having sum(Pages) = (SELECT MAX(total_pages)
						FROM (SELECT SUM(pages) AS total_pages
						from [dbo].[books]
						GROUP BY Izd) AS subquery);

--7. Отобразить издательство, у которого наибольшее количество книг по программированию (2 способа).
select top 1 Izd, COUNT(*) as BookCount
from [dbo].[books]
where Themes = N'программирование'
group by Izd
order by BookCount desc

select Izd, COUNT(*) as BookCount
from [dbo].[books]
where Themes = N'программирование'
group by Izd
having count(*) = (select max(BookCount) 
					from (
						select COUNT(*) as BookCount
						from [dbo].[books]
						where Themes = N'программирование'
						group by Izd) as subquery
						)

--8. Определить, сколько издано книг по каждой тематике.
select Themes, count(*)
from [dbo].[books]
group by Themes

--9. Отобразить самую дешевую книгу в каждой из следующих тематик: Программирование, Базы данных клиент-сервер, Мультимедиа.
SELECT [Name], Themes, price
FROM books
WHERE (Themes = N'Программирование' AND price = (SELECT MIN(price) FROM books WHERE Themes = N'Программирование'))
   OR (Themes = N'Базы данных клиент-сервер' AND price = (SELECT MIN(price) FROM books WHERE Themes = N'Базы данных клиент-сервер'))
   OR (Themes = N'Мультимедиа' AND price = (SELECT MIN(price) FROM books WHERE Themes = N'Мультимедиа'));

select [Name], Themes, price
from books
where Themes in (N'Программирование', N'Базы данных клиент-сервер', N'Мультимедиа')
  and price = (
      select min(price)
      from books b2
      where b2.Themes = books.Themes
  );
--10. Показать издательства и самую старую книгу для каждого из них.
select b.Izd, count(*), min(b.[Date]), (select top 1 b1.[Name] from dbo.books as b1 where b1.Izd = b.Izd and b1.[Date] = min(b.[Date]))
from [dbo].[books] as b
group by b.Izd

--11. Показать количество выпущенных книг-новинок по каждому издательству.
select Izd, count(*)
from [dbo].[books]
where New = 1
group by Izd

--12. Отобразить издательство, у которого наибольшее количество книг-новинок (2 способа).
select top 1 Izd, count(*) as [count]
from [dbo].[books]
where New = 1
group by Izd
order by [count] desc

select Izd, count(*) as [count]
from [dbo].[books]
where New = 1
group by Izd
having count(*) = (select max([count]) 
					from(
						select top 1 count(*) as [count]
						from [dbo].[books]
						where New = 1
						group by Izd
						order by [count] desc) as subquery
						)

